import React, { useContext, useEffect } from 'react';
import { getBookById, getReviews } from "../services/ApiService"
import { BookContext } from '../context/BookContext';
import { useParams } from 'react-router-dom';
import ReviewTableRow from './ReviewTableRow'
import { ReviewContext } from '../context/ReveiwContext';

export default function BookInform(){
  const { id } = useParams();
  const {book,updateBook} = useContext(BookContext);
  const { reviews, updateReviews} = useContext(ReviewContext);

  useEffect(()=>{
    async function fetchData(){
      try{
        const book = await getBookById(id);
        updateBook(book);
        const reviews = await getReviews();
        updateReviews(reviews);
        
      }catch(error){
        console.error('error',error);
      }
    }
    fetchData();
  },[id,updateBook,updateReviews]);

  const handleCreateReview = () => {
    history.push('/newReview'); // 새 리뷰 생성 페이지로 이동
  };
  

  
  if (book.review_count == 0) return (
    <div>
        <h2>책의 정보</h2>
        <hr/>
        <div>
            <p>Title : {book.title}</p>
            <p>Author: {book.author}</p>
            <p>Genre: {book.genre}</p>
            <p>Summary: {book.summary}</p>
            <p>Review Count: {book.review_count}</p>
        </div>
        <hr/>
        <h2>리뷰 목록</h2>
        </div>
    );
  

    


  return (
    <div>
        <h2>책의 정보</h2>
        <hr/>
        <div>
            <p>Title : {book.title}</p>
            <p>Author: {book.author}</p>
            <p>Genre: {book.genre}</p>
            <p>Summary: {book.summary}</p>
            <p>Review Count: {book.review_count}</p>
        </div>
        <hr/>
        <h2>리뷰 목록</h2>

        <table className="reviews-table"></table>
        <thead>
            <tr>
                <th>id</th>
                <th>이름</th>
                <th>점수</th>
                <th>리뷰</th>
            </tr>
        </thead>
        <tbody>
            {reviews.map(review=><ReviewTableRow key={review.id}{...review}/>)}
        </tbody>
    </div>
  );
}
