import React, { useState, useEffect,useRef } from 'react';
import axios from 'axios';
import { createBook, getBooks } from "../services/ApiService"

const BookList = () => {
  const [books, setBooks] = useState([]);

  useEffect(()=>{
    async function fetchData(){
      try{
        const books = await getBooks();
        setBooks(books);
      }catch(error){
        console.error('error',error);
      }
    }

    fetchData();
  },[]);

  
  const[createForm , setForm] = newState({
    title : "",
    author : "",
    genre : "",
    summary : ""
  })

  return (
    <div>
      <h1>Book List</h1>

      <table className="book-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Author</th>
            <th>Genre</th>
            <th>Summary</th>
            <th>Review Count</th>
          </tr>
        </thead>
        <tbody>
          {(
            books.map(book => (
              <tr key={book.id}>
                <td style={{ textAlign: 'center' }}>{book.id}</td>
                <td style={{ textAlign: 'left', paddingLeft: '10px' }}>{book.title}</td>
                <td style={{ textAlign: 'left', paddingLeft: '10px' }}>{book.author}</td>
                <td style={{ textAlign: 'left', paddingLeft: '10px' }}>{book.genre}</td>
                <td style={{ textAlign: 'left', paddingLeft: '10px' }}>{book.summary}</td>
                <td style={{ textAlign: 'center' }}>{book.review_count}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
          <hr></hr>
      <div>
          
          <h7>제목</h7>
          <input type = "text" name="title" value ={createForm.title} placeholder="Enter Title"/><br/>
          <h7>저자</h7>
          <input type = "text" name="author" value ={createForm.author} placeholder="Enter Author"/><br/>
          <h7>장르</h7>
          <input type = "text" name="genre"value ={createForm.genre}  placeholder="Enter Genre"/><br/>
          <h7>줄거리</h7>
          <input type = "textarea" name="summary"value ={createForm.summary} placeholder="Enter Summary"/><br/>
          <button type='submit'>생성</button>

      </div>
    </div>
  );
};

  
export default BookList;
