import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BookList = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get('http://localhost:8080/books'); // 서버에서 책 데이터를 가져오는 엔드포인트로의 요청
        console.log(response.data);
        setBooks(response.data); // 가져온 데이터를 상태에 설정
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    };

    fetchBooks(); // useEffect가 마운트될 때 한 번 실행
  }, []); // 빈 배열을 전달하여 컴포넌트가 마운트될 때 한 번만 실행

  const handleCreateBook = async () => {
    try {
      // 책 생성 API를 호출하여 새 책을 생성하는 코드
      // 예: const response = await axios.post('http://localhost:8080/books', { title: 'New Book', author: 'Author Name', ... });
      // 생성 후, 필요하다면 새 책을 다시 가져오는 로직을 추가할 수 있습니다.
    } catch (error) {
      console.error('Error creating book:', error);
    }
  };

  return (
    <div>
      <h1>Book List</h1>
      <button onClick={handleCreateBook}>Create New Book</button>
      <table className = "book-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Author</th>
            <th>Genre</th>
            <th>Summary</th>
            <th>Review Count</th>
          </tr>
        </thead>
        <tbody>
          {books.map(book => (
            <tr key={book.id}>
            <td style={{ textAlign: 'center' }}>{book.id}</td>
            <td style={{ textAlign: 'left', paddingLeft: '10px' }}>{book.title}</td>
            <td style={{ textAlign: 'left', paddingLeft: '10px' }}>{book.author}</td>
            <td style={{ textAlign: 'left', paddingLeft: '10px' }}>{book.genre}</td>
            <td style={{ textAlign: 'left', paddingLeft: '10px' }}>{book.summary}</td>
            <td style={{ textAlign: 'left',paddingLeft: '10px' }}>{book.review_count}</td>
          </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default BookList;
