import React, { useContext, useState, useRef } from 'react';
import { deleteReviewById, updateReviewById } from "../services/ApiService";
import { ReviewContext } from '../context/ReveiwContext';
import { useNavigate } from 'react-router-dom';

export default function ReviewTableRow({ id, name, grade, content, book_id }) {
  const { removeReviewById } = useContext(ReviewContext);
  const { updateReview } = useContext(ReviewContext);

  const nameRef = useRef();
  const gradeRef = useRef();
  const contentRef = useRef();

  const navigate = useNavigate();

  const [isEditing, setIsEditing] = useState(false);

  async function deleteReview() {
    try {
      await deleteReviewById(id);
      removeReviewById(id);
    } catch (error) {
      console.error('Error deleting review:', error);
    }
  }

  async function editReview() {
    setIsEditing(true);
  }

  async function saveReview() {
    const updatedReview = {
      id,
      name: nameRef.current.value,
      grade: gradeRef.current.value,
      content: contentRef.current.value,
      book_id
    };

    try {
      await updateReviewById(id, updatedReview);
      updateReview(updatedReview);
      setIsEditing(false);
    } catch (error) {
      console.error('Error updating review:', error);
    }
  }

  async function cancelEdit() {
    setIsEditing(false);
  }

  return (
    <tr>
      <th>{id}</th>
      <td>{isEditing ? <input type="text" defaultValue={name} ref={nameRef} /> : name}</td>
      <td>{isEditing ? <input type="text" defaultValue={grade} ref={gradeRef} /> : grade}</td>
      <td>{isEditing ? <input type="text" defaultValue={content} ref={contentRef} /> : content}</td>
      <td><button onClick={deleteReview}>삭제</button></td>
      <td>
        {isEditing ? (
          <>
            <button onClick={saveReview}>저장</button>
            <button onClick={cancelEdit}>취소</button>
          </>
        ) : (
          <button onClick={editReview}>수정</button>
        )}
      </td>
    </tr>
  );
}
