import React, { useContext,useState, useEffect } from 'react';
import { deleteBookById, getBooks } from "../services/ApiService"
import { BookContext } from '../context/BookContext';
const BookList = () => {
  const [books, setBooks] = useState([]);

  useEffect(()=>{
    async function fetchData(){
      try{
        const books = await getBooks();
        setBooks(books);
      }catch(error){
        console.error('error',error);
      }
    }

    fetchData();
  },[]);

  const {removeBookById} = useContext(BookContext);
  async function deleteBook(id) {
    try {
      await deleteBookById(id);
      removeBookById(id);

    } catch (error) {
      console.error('Error fetching products:', error);
    }
  }


  return (
    <div>
      <h1>Book List</h1>

      <table className="book-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Author</th>
            <th>Genre</th>
            <th>Summary</th>
            <th>Review Count</th>
          </tr>
        </thead>
        <tbody>
          {(
            books.map(book => (
              <tr key={book.id}>
                <td style={{ textAlign: 'center' }}>{book.id}</td>
                <td style={{ textAlign: 'left', paddingLeft: '10px' }}>{book.title}</td>
                <td style={{ textAlign: 'left', paddingLeft: '10px' }}>{book.author}</td>
                <td style={{ textAlign: 'left', paddingLeft: '10px' }}>{book.genre}</td>
                <td style={{ textAlign: 'left', paddingLeft: '10px' }}>{book.summary}</td>
                <td style={{ textAlign: 'center' }}>{book.review_count}</td>
                
                <button onClick={deleteBook(book.id)}>삭제</button>
              </tr>
            ))
          )}
        </tbody>
      </table>
          <hr></hr>
      </div>
  );
};

  
export default BookList;
