import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get('http://localhost:8080/books');
        setBooks(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    };

    fetchBooks();
  }, []);

  const handleCreateBook = async () => {
    try {
      // 책 생성 API를 호출하여 새 책을 생성하는 코드
      // 예: const response = await axios.post('http://localhost:8080/books', { title: 'New Book', author: 'Author Name', ... });
      // 생성 후, 필요하다면 새 책을 다시 가져오는 로직을 추가할 수 있습니다.
    } catch (error) {
      console.error('Error creating book:', error);
    }
  };

  return (
    <div>
      <h1>Book List</h1>
      <button onClick={handleCreateBook}>Create New Book</button>
      <table className="book-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Author</th>
            <th>Genre</th>
            <th>Summary</th>
            <th>Review Count</th>
          </tr>
        </thead>
        <tbody>
          {loading ? (
            <tr>
              <td colSpan="6">Loading...</td>
            </tr>
          ) : (
            books.map(book => (
              <tr key={book.id}>
                <td style={{ textAlign: 'center' }}>{book.id}</td>
                <td style={{ textAlign: 'left', paddingLeft: '10px' }}>{book.title}</td>
                <td style={{ textAlign: 'left', paddingLeft: '10px' }}>{book.author}</td>
                <td style={{ textAlign: 'left', paddingLeft: '10px' }}>{book.genre}</td>
                <td style={{ textAlign: 'left', paddingLeft: '10px' }}>{book.summary}</td>
                <td style={{ textAlign: 'center' }}>{book.reviewCount}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default BookList;
