import React, { useContext, useRef, useState } from "react";
import { updateBookById } from "../services/ApiService";
import { BookContext } from "../context/BookContext";
import { useNavigate, useParams } from 'react-router-dom';

export default function BookUpdateForm() {
  const { book, updateBook } = useContext(BookContext);
  const navigate = useNavigate();
  const { id } = useParams();

  const titleRef = useRef();
  const authorRef = useRef();
  const genreRef = useRef();
  const summaryRef = useRef();
  const [errorMessage, setErrorMessage] = useState("");

  async function handleUpdate(target) {
    target.preventDefault();

    if (
      !titleRef.current.value ||
      !authorRef.current.value ||
      !genreRef.current.value ||
      !summaryRef.current.value
    ) {
      setErrorMessage("모든 필드를 입력하세요.");
      return;
    }

    try {
      const updatedBook = {
        title: titleRef.current.value,
        author: authorRef.current.value,
        genre: genreRef.current.value,
        summary: summaryRef.current.value
      };

      const response = await updateBookById(id, updatedBook);
      updateBook(response);
      navigate(`/book/${id}`);

    } catch (error) {
      console.error('Error updating book:', error);
    }
  }

  function goToMain() {
    navigate(`/book`);
  }

  return (
    <div className="update-form">
      <div className="header-container">
        <h1>책 정보 업데이트</h1>
        {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
        <div className="main-btn">
          <button onClick={goToMain}>취소</button>
        </div>
      </div>
      <hr />
      <div>
        제목
        <input
          type="text"
          name="title"
          defaultValue={book.title}
          ref={titleRef}
        />
        저자
        <input
          type="text"
          name="author"
          defaultValue={book.author}
          ref={authorRef}
        />
        장르
        <input
          type="text"
          name="genre"
          defaultValue={book.genre}
          ref={genreRef}
        />
        줄거리
        <textarea
          type="text"
          name="summary"
          defaultValue={book.summary}
          ref={summaryRef}
        />
      </div>
      <button onClick={handleUpdate} type="submit">
        업데이트
      </button>
    </div>
  );
}
