import React, { useState,useContext, useRef } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { BookContext } from '../context/BookContext';
import {createBook} from "../services/ApiService";

export default function BookCreate(){
  const navigate = useNavigate();
  const titleRef = useRef();
  const authorRef = useRef();
  const genreRef = useRef();
  const summaryRef = useRef();

  const {addBook} = useContext(BookContext);

  async function add(target) {
    target.preventDefault();

    try{
      const newBook = {
        title: titleRef.current.value,
        author: authorRef.current.value,
        genre: genreRef.current.value,
        summary: summaryRef.current.value
      };

      console.log(newBook)

      const response = await createBook(newBook);
      console.log(response)
      addBook(response);
      navigate('/${response.id}');

    } catch(error){
        console.error('ERROR',error);
    }
  }

  return (
      <form>
      <div>
        <label htmlFor="title">Title</label>
        <input ref={titleRef} type="text" id="title" aria-describedby="titleHelp" />
        <div id="titleHelp" >Input the product title here.</div>
      </div>
      <div >
        <div>
          <div>
            <label htmlFor="author">Price</label>
            <input ref={authorRef} type="text" id="author" />
          </div>
          <div>
            <label htmlFor="genre" >Quantity</label>
            <input ref={genreRef} type="text" id="genre" />
          </div>
          <div>
            <label htmlFor="summary" >Quantity</label>
            <input ref={summaryRef} type="text" id="summary" />
          </div>
        </div>

      </div>
      <button onClick={add} type="submit" className="btn btn-primary">Add</button>
    </form>
  );
}
