import React, {useContext, useRef} from "react";
import { createReview } from "../services/ApiService";
import { ReviewContext } from "../context/ReveiwContext";
import {useNavigate} from'react-router-dom';

export default function ReviewCreateForm(){
    const {addReview} = useContext(ReviewContext);

    const nameRef= useRef();
    const gradeRef= useRef();
    const contentRef= useRef();

    async function add(target) {
        target.preventDefault();
        try {
          const newReview = {
            name: nameRef.current.value,
            grade: gradeRef.current.value,
            content: contentRef.current.value,
          };
    
          console.log(newReview)
    
          const response = await createReview(newReview);
          console.log(response)
          addReview(response);

    
        } catch (error) {
          console.error('Error', error);
        }
      }

    return(<div><h1>리뷰 등록</h1><hr/>
    <div>
        제목
        <br/>
        <hr/>
        이름
        <input 
        type = "text" 
        name="name" 
        ref ={nameRef}
        placeholder="Enter name"
        /><br/>
        점수
        <input 
        type = "text" 
        name="grade"
        ref ={gradeRef}
        placeholder="Enter grade"
        /><br/>
        코멘트
        <textarea 
        type = "text" 
        name="content"
        ref ={contentRef}
        placeholder="Enter Comment"
        /><br/>
      </div>

      <button onClick={add} type="submit">생성</button>
  </div>      
    );
}
