import React, { useContext ,useState,useRef} from 'react';
import { createReview, deleteReviewById, updateReviewById } from "../services/ApiService"
import { ReviewContext } from '../context/ReveiwContext';
import { useNavigate } from 'react-router-dom';


export default function ReviewTableRow({id,name,grade,content,book_id}){
  const {removeReviewById} = useContext(ReviewContext);
  const [showReviewForm, setShowReviewForm] = useState(false);
  const nameRef= useRef();
  const gradeRef= useRef();
  const contentRef= useRef();
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();
  updateReview = useContext(ReviewContext);
  async function deleteReview() {
    try {
      await deleteReviewById(id);
      removeReviewById(id);

    } catch (error) {
      console.error('Error fetching products:', error);
    }
  }

  const handleUpdateReview = () => {
    if(showReviewForm){
        setShowReviewForm(false);
    }
    else{
        setShowReviewForm(true);
    }
   
  };

  async function update(target) {
    target.preventDefault();

    if (
      !nameRef.current.value ||
      !gradeRef.current.value ||
      !contentRef.current.value
    ) {
      setErrorMessage("모든 필드를 입력하세요.");
      return;
    }

    try {
      const newReview = {
        name: nameRef.current.value,
        grade: gradeRef.current.value,
        content: contentRef.current.value,
        book_id: id
      };

      console.log(newReview)

      const response = await updateReviewById(id,newReview);
      console.log(response)
      updateReview(response);
      navigate(`/book/${id}`);

    } catch (error) {
      console.error('Error', error);
    }
  }


  return (
    <tr>
        <th>{id}</th>
        <td>{name}</td>
        <td>{grade}</td>
        <td>{content}</td>
        <td><button onClick={deleteReview} >삭제</button></td>
        <td><button onclick={handleUpdateReview} >수정</button></td>
        {showReviewForm && (
        <div>
          이름
          <input 
            type="text" 
            name="name" 
            ref={nameRef} 
            placeholder="Enter name"
          /><br />
          점수
          <input 
            type="text" 
            name="grade" 
            ref={gradeRef} 
            placeholder="Enter grade"
          /><br />
          코멘트
          <textarea 
            type="text" 
            name="content" 
            ref={contentRef} 
            placeholder="Enter Comment"
          /><br />
          <button onClick={update} type="submit">완료</button>
        </div>
      )}
    </tr>
    
  );
}
