import { NavLink } from "react-router-dom";
import {deleteBookById} from "../services/ApiService";
import {useContext} from 'react';
import {BookContext} from "../context/BookContext";

export default function BookTableRow ({id, title, author, genre,summary}) {

  const {removeBookById} = useContext(BookContext);

  async function deleteBook() {
    try {
      await deleteBookById(id);
      removeBookById(id);

    } catch (error) {
      console.error('Error fetching Books:', error);
    }
  }

  return(
    <tr>
      <th scope="row">{id}</th>
      <td>{title}</td>
      <td>{author}</td>
      <td>{genre}</td>
      <td>{summary}</td>
      <td>
        <div className="btn-group">
          <NavLink className="btn btn-info" to={`/${id}`}>View</NavLink>
          <NavLink className="btn btn-light" to={`/${id}/edit`}>Edit</NavLink>
          <button onClick={deleteBook} className="btn btn-danger">Delete</button>
        </div>
      </td>
    </tr>
  );
}
