import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BookList = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/books'); // 서버에서 책 데이터를 가져오는 엔드포인트로의 요청
        setBooks(response.data); // 가져온 데이터를 상태에 설정
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    };

    fetchBooks(); // useEffect가 마운트될 때 한 번 실행
  }, []); // 빈 배열을 전달하여 컴포넌트가 마운트될 때 한 번만 실행

  return (
    <div>
      <h1>Book List</h1>
      <ul>
        {books.map(book => (
          <li key={book.id}>
            <strong>{book.title}</strong> by {book.author}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BookList;
